import * as turf from "@turf/turf";
import { Feature, Geometry } from "geojson";

export const hasOverlap = (
  newFeature: Feature<Geometry>,
  existing: Feature<Geometry>[]
): boolean => {
  if (!newFeature?.geometry) return false;

  for (const f of existing) {
    if (!f?.geometry) continue;

    // ❌ LineString ke sath intersect nahi
    if (
      f.geometry.type === "LineString" ||
      newFeature.geometry.type === "LineString"
    ) {
      continue;
    }

    try {
      const intersection = turf.intersect(
        f as any,
        newFeature as any
      );

      if (intersection) return true;
    } catch (err) {
      // ❗ Turf kabhi crash kare to ignore
      console.warn("Overlap check skipped:", err);
      continue;
    }
  }

  return false;
};
