import { useEffect, useRef, useState } from "react";
import {
  MapContainer,
  TileLayer,
  FeatureGroup,
  Marker,
  Tooltip,
  Polygon,
  Circle,
  Polyline,
  useMap,
} from "react-leaflet";
import { EditControl } from "react-leaflet-draw";
import L from "leaflet";
import * as turf from "@turf/turf";
import { Feature, Geometry, Polygon as GeoPolygon } from "geojson";

import { trimPolygon, isFullyInside } from "../utils/geometry";
import { MAX_SHAPES, ShapeType } from "../config";

import "leaflet/dist/leaflet.css";
import "leaflet-draw/dist/leaflet.draw.css";
import "../styles/map.css";

/* ---------------- MAP REF ---------------- */
const MapRefSetter = ({ mapRef }: { mapRef: React.MutableRefObject<L.Map | null> }) => {
  const map = useMap();
  useEffect(() => {
    mapRef.current = map;
  }, [map, mapRef]);
  return null;
};

/* ---------------- CUSTOM ZOOM ---------------- */
const CustomZoom = () => {
  const map = useMap();
  useEffect(() => {
    const zoomIn = () => map.zoomIn();
    const zoomOut = () => map.zoomOut();
    window.addEventListener("map-zoom-in", zoomIn);
    window.addEventListener("map-zoom-out", zoomOut);
    return () => {
      window.removeEventListener("map-zoom-in", zoomIn);
      window.removeEventListener("map-zoom-out", zoomOut);
    };
  }, [map]);
  return null;
};

/* ---------------- DRAW ZOOM CONTROL ---------------- */
const DrawZoomControl = () => {
  const map = useMap();

  useEffect(() => {
    const ZoomControl = L.Control.extend({
      onAdd: function () {
        const container = L.DomUtil.create(
          "div",
          "leaflet-bar draw-zoom-control"
        );

        const zoomIn = L.DomUtil.create("a", "", container);
        zoomIn.innerHTML = "+";

        const zoomOut = L.DomUtil.create("a", "", container);
        zoomOut.innerHTML = "‚àí";

        L.DomEvent.disableClickPropagation(container);
        L.DomEvent.disableScrollPropagation(container);

        L.DomEvent.on(zoomIn, "click", () => map.zoomIn());
        L.DomEvent.on(zoomOut, "click", () => map.zoomOut());

        return container;
      },
    });

    const control = new ZoomControl({ position: "topright" }) as L.Control;
    map.addControl(control);

    // ‚úÖ CLEANUP ‚Äî EXPLICIT VOID
    return () => {
      map.removeControl(control);
    };
  }, [map]);

  return null;
};


/* ---------------- COLORS ---------------- */
const SHAPE_COLORS = [
  { stroke: "#ff6b6b", fill: "#ff8787" },
  { stroke: "#4dabf7", fill: "#74c0fc" },
  { stroke: "#f59f00", fill: "#ffc078" },
  { stroke: "#37b24d", fill: "#8ce99a" },
];

/* ================= MAP VIEW ================= */
const MapView = () => {
  const [features, setFeatures] = useState<Feature<Geometry>[]>([]);
  const [searchText, setSearchText] = useState("");
  const [searchCoords, setSearchCoords] = useState<[number, number] | null>(null);
  const [theme, setTheme] = useState<"dark" | "light">("light");
  const mapRef = useRef<L.Map | null>(null);

  /* ---------------- CREATED ---------------- */
  const onCreated = (e: any) => {
    const layer = e.layer;
    let geojson = layer.toGeoJSON() as Feature<Geometry>;
    const rawType = geojson.geometry.type.toLowerCase();

    let shapeType: ShapeType;
    if (rawType === "linestring") shapeType = "linestring";
    else if (layer instanceof L.Circle) shapeType = "circle";
    else if (layer instanceof L.Rectangle) shapeType = "rectangle";
    else shapeType = "polygon";

    /* ---------- MAX LIMIT ---------- */
    const count = features.filter(f => f.properties?.shapeType === shapeType).length;
    if (count >= MAX_SHAPES[shapeType]) {
      alert(`‚ùå Maximum ${shapeType} limit reached`);
      layer.remove();
      return;
    }

    /* ---------- LINESTRING ---------- */
    if (shapeType === "linestring") {
      geojson.properties = { id: Date.now(), shapeType };
      setFeatures(prev => [...prev, geojson]);
      return;
    }

    /* ---------- CONVERT TO POLYGON ---------- */
    let newPolygon: Feature<GeoPolygon>;

    if (layer instanceof L.Circle) {
      const c = layer.getLatLng();
      newPolygon = turf.circle([c.lng, c.lat], layer.getRadius(), {
        steps: 64,
        units: "meters",
      }) as Feature<GeoPolygon>;
    } else {
      newPolygon = geojson as Feature<GeoPolygon>;
    }

    /* ---------- POLYGON RULES ---------- */
    const existingPolygons = features.filter(
      f => f.geometry.type === "Polygon"
    ) as Feature<GeoPolygon>[];

    let finalPolygon = newPolygon;

    for (const existing of existingPolygons) {
      // üö´ FULL ENCLOSURE (INTERVIEWER CHECK)
      if (isFullyInside(finalPolygon, existing)) {
        alert("‚ùå Polygon fully encloses or is enclosed by another polygon");
        layer.remove();
        return;
      }

      // ‚úÇÔ∏è PARTIAL OVERLAP ‚Üí AUTO TRIM
      if (turf.booleanIntersects(finalPolygon, existing)) {
        const trimmed = trimPolygon(finalPolygon, existing);
        if (!trimmed) {
          alert("‚ùå Polygon fully overlapped");
          layer.remove();
          return;
        }
        finalPolygon = trimmed;
      }
    }

    geojson = finalPolygon as Feature<Geometry>;

    /* ---------- STYLE ---------- */
    const color = SHAPE_COLORS[Math.floor(Math.random() * SHAPE_COLORS.length)];
    if ((layer as any).setStyle) {
      (layer as any).setStyle({
        color: color.stroke,
        fillColor: color.fill,
        fillOpacity: 0.4,
        weight: 3,
      });
    }

    geojson.properties = {
      id: Date.now(),
      shapeType,
      strokeColor: color.stroke,
      fillColor: color.fill,
    };

    setFeatures(prev => [...prev, geojson]);
  };

  /* ---------------- SEARCH ---------------- */
  const handleSearch = async () => {
    if (!searchText) return;
    const res = await fetch(
      `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(searchText)}`
    );
    const data = await res.json();
    if (data?.length) {
      const lat = +data[0].lat;
      const lon = +data[0].lon;
      setSearchCoords([lat, lon]);
      mapRef.current?.setView([lat, lon], 9);
    } else alert("Location not found");
  };

  /* ================= RENDER ================= */
  return (
    <MapContainer
      center={[20.5937, 78.9629]}
      zoom={5}
      zoomControl={false}
      className="map-container"
    >
      <MapRefSetter mapRef={mapRef} />
      <CustomZoom />

      <TileLayer
        url={
          theme === "dark"
            ? "https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png"
            : "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        }
      />

      <FeatureGroup>
        <EditControl
          position="topright"
          onCreated={onCreated}
          draw={{
            polygon: true,
            rectangle: true,
            circle: true,
            polyline: true,
            marker: false,
            circlemarker: false,
          }}
        />
      </FeatureGroup>

      <DrawZoomControl />

      {searchCoords && (
        <Marker position={searchCoords}>
          <Tooltip permanent>{searchText}</Tooltip>
        </Marker>
      )}

      {features.map((f, i) => {
        const stroke = f.properties?.strokeColor || "#000";
        const fill = f.properties?.fillColor || "#000";

        if (f.geometry.type === "Polygon")
          return (
            <Polygon
              key={i}
              positions={(f.geometry as any).coordinates[0].map((p: any) => [p[1], p[0]])}
              pathOptions={{ color: stroke, fillColor: fill, fillOpacity: 0.4 }}
            />
          );

        if (f.geometry.type === "LineString")
          return (
            <Polyline
              key={i}
              positions={(f.geometry as any).coordinates.map((p: any) => [p[1], p[0]])}
              pathOptions={{ color: stroke, weight: 3 }}
            />
          );

        return null;
      })}
    </MapContainer>
  );
};

export default MapView;
