import { useEffect, useRef, useState } from "react";
import {
  MapContainer,
  TileLayer,
  FeatureGroup,
  Marker,
  Tooltip,
  Polygon,
  Circle,
  Polyline,
  useMap,
} from "react-leaflet";
import { EditControl } from "react-leaflet-draw";
import L from "leaflet";
import * as turf from "@turf/turf";
import { Feature, Geometry } from "geojson";

import { hasOverlap } from "../utils/geometry";
import { MAX_SHAPES, ShapeType } from "../config";

import "leaflet/dist/leaflet.css";
import "leaflet-draw/dist/leaflet.draw.css";
import "../styles/map.css";

/* ---------------- MAP REF ---------------- */
const MapRefSetter = ({ mapRef }: { mapRef: React.MutableRefObject<L.Map | null> }) => {
  const map = useMap();
  useEffect(() => {
    mapRef.current = map;
  }, [map, mapRef]);
  return null;
};

/* ---------------- ZOOM CONTROL ---------------- */
const DrawZoomControl = () => {
  const map = useMap();

  useEffect(() => {
    const ZoomControl = L.Control.extend({
      onAdd() {
        const c = L.DomUtil.create("div", "leaflet-bar draw-zoom-control");

        const zoomIn = L.DomUtil.create("a", "", c);
        zoomIn.innerHTML = "+";

        const zoomOut = L.DomUtil.create("a", "", c);
        zoomOut.innerHTML = "−";

        L.DomEvent.disableClickPropagation(c);
        L.DomEvent.on(zoomIn, "click", () => map.zoomIn());
        L.DomEvent.on(zoomOut, "click", () => map.zoomOut());

        return c;
      },
    });

    const control = new ZoomControl({ position: "topright" }) as L.Control;
    map.addControl(control);

    return () => {
      map.removeControl(control); // ✅ void return
    };
  }, [map]);

  return null;
};

/* ---------------- COLORS ---------------- */
const SHAPE_COLORS = [
  { stroke: "#ff6b6b", fill: "#ff8787" },
  { stroke: "#4dabf7", fill: "#74c0fc" },
  { stroke: "#f59f00", fill: "#ffc078" },
  { stroke: "#37b24d", fill: "#8ce99a" },
];

/* ================= MAP VIEW ================= */
const MapView = () => {
  const [features, setFeatures] = useState<Feature<Geometry>[]>([]);
  const mapRef = useRef<L.Map | null>(null);

  /* ------------ ON CREATE ------------ */
  const onCreated = (e: any) => {
    const layer = e.layer;
    let geojson = layer.toGeoJSON() as Feature<Geometry>;

    let shapeType: ShapeType;

    if (layer instanceof L.Circle) shapeType = "circle";
    else if (layer instanceof L.Rectangle) shapeType = "rectangle";
    else if (geojson.geometry.type === "LineString") shapeType = "linestring";
    else shapeType = "polygon";

    /* ---- SHAPE LIMIT ---- */
    const count = features.filter(
      f => f.properties?.shapeType === shapeType
    ).length;

    if (count >= MAX_SHAPES[shapeType]) {
      alert(`Maximum ${shapeType} limit reached`);
      layer.remove();
      return;
    }

    /* ---- LINESTRING NO OVERLAP ---- */
    if (shapeType === "linestring") {
      geojson.properties = { shapeType };
      setFeatures(prev => [...prev, geojson]);
      return;
    }

    /* ---- CIRCLE → SAFE POLYGON ---- */
    if (layer instanceof L.Circle) {
      const center = layer.getLatLng();
      const radius = layer.getRadius();

      geojson = turf.circle(
        [center.lng, center.lat],
        radius,
        { steps: 32, units: "meters" }
      ) as Feature<Geometry>;

      geojson.properties = { shapeType, radius };
    }

    /* ---- OVERLAP CHECK (SAFE) ---- */
    const safeFeatures = features.filter(
      f => f.geometry && f.geometry.type !== "LineString"
    );

    if (safeFeatures.length > 0 && hasOverlap(geojson, safeFeatures)) {
      alert("❌ Shape overlaps existing shape");
      layer.remove();
      return;
    }

    /* ---- STYLE ---- */
    const color = SHAPE_COLORS[Math.floor(Math.random() * SHAPE_COLORS.length)];
    if (layer.setStyle) {
      layer.setStyle({
        color: color.stroke,
        fillColor: color.fill,
        fillOpacity: 0.4,
        weight: 3,
      });
    }

    geojson.properties = {
      ...geojson.properties,
      shapeType,
      strokeColor: color.stroke,
      fillColor: color.fill,
    };

    setFeatures(prev => [...prev, geojson]);
  };

  /* ------------ DELETE ------------ */
  const onDeleted = (e: any) => {
    e.layers.eachLayer((layer: any) => {
      const gj = layer.toGeoJSON();
      setFeatures(prev => prev.filter(f => f !== gj));
    });
  };

  return (
    <div className="map-wrapper">
      <MapContainer
        center={[20.5937, 78.9629]}
        zoom={5}
        zoomControl={false}
        className="map-container"
      >
        <MapRefSetter mapRef={mapRef} />

        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        <FeatureGroup>
          <EditControl
            position="topright"
            onCreated={onCreated}
            onDeleted={onDeleted}
            draw={{
              polygon: true,
              rectangle: true,
              circle: true,
              polyline: true,
              marker: false,
              circlemarker: false,
            }}
          />
        </FeatureGroup>

        <DrawZoomControl />

        {features.map((f, i) => {
          const stroke = f.properties?.strokeColor;
          const fill = f.properties?.fillColor;

          if (f.geometry.type === "Polygon")
            return (
              <Polygon
                key={i}
                positions={(f.geometry as any).coordinates[0].map(
                  (p: number[]) => [p[1], p[0]]
                )}
                pathOptions={{ color: stroke, fillColor: fill, fillOpacity: 0.4 }}
              />
            );

          if (f.geometry.type === "LineString")
            return (
              <Polyline
                key={i}
                positions={(f.geometry as any).coordinates.map(
                  (p: number[]) => [p[1], p[0]]
                )}
                pathOptions={{ color: stroke }}
              />
            );

          return null;
        })}
      </MapContainer>
    </div>
  );
};

export default MapView;
