import { Feature, Polygon } from "geojson";
import { difference, booleanContains, booleanIntersects } from "@turf/turf";



export const applyPolygonRules = (
  newFeature: Feature<Geometry>,
  existing: Feature<Geometry>[]
): Feature<Geometry> | null => {
  for (const old of existing) {
    if (!old.geometry) continue;

    if (
      booleanContains(newFeature as any, old as any) ||
      booleanContains(old as any, newFeature as any)
    ) {
      return null;
    }

    if (booleanIntersects(newFeature as any, old as any)) {
      return null;
    }
  }

  return newFeature;
};

export const hasOverlap = (
  newFeature: Feature<Geometry>,
  features: Feature<Geometry>[]
): boolean => {
  for (const f of features) {
    if (
      f.geometry.type !== "LineString" &&
      newFeature.geometry.type !== "LineString"
    ) {
      if (booleanIntersects(newFeature as any, f as any)) {
        return true;
      }
    }
  }
  return false;
};

export const trimPolygon = (
  newPoly: Feature<Polygon>,
  existingPoly: Feature<Polygon>
): Feature<Polygon> | null => {
  try {
    const trimmed = difference(newPoly, existingPoly);

    if (!trimmed) return null;

    return trimmed as Feature<Polygon>;
  } catch (err) {
    console.warn("Auto-trim failed:", err);
    return null;
  }
};
