import { useEffect, useRef, useState } from "react";
import {
  MapContainer,
  TileLayer,
  FeatureGroup,
  Marker,
  Tooltip,
  Polygon,
  Circle,
  Polyline,
  useMap,
} from "react-leaflet";
import { EditControl } from "react-leaflet-draw";
import L from "leaflet";
import * as turf from "@turf/turf";
import { Feature, Geometry } from "geojson";

import { hasOverlap } from "../utils/geometry";
import { MAX_SHAPES, ShapeType } from "../config";

import "leaflet/dist/leaflet.css";
import "leaflet-draw/dist/leaflet.draw.css";
import "../styles/map.css";

/* ---------------- Helpers ---------------- */

const MapRefSetter = ({ mapRef }: { mapRef: React.MutableRefObject<L.Map | null> }) => {
  const map = useMap();
  useEffect(() => {
    mapRef.current = map;
  }, [map, mapRef]);
  return null;
};

const CustomZoom = () => {
  const map = useMap();
  useEffect(() => {
    const zoomIn = () => map.zoomIn();
    const zoomOut = () => map.zoomOut();
    window.addEventListener("map-zoom-in", zoomIn);
    window.addEventListener("map-zoom-out", zoomOut);
    return () => {
      window.removeEventListener("map-zoom-in", zoomIn);
      window.removeEventListener("map-zoom-out", zoomOut);
    };
  }, [map]);
  return null;
};

const DrawZoomControl = () => {
  const map = useMap();
  useEffect(() => {
    const ZoomControl = L.Control.extend({
      onAdd() {
        const c = L.DomUtil.create("div", "leaflet-bar draw-zoom-control");
        const zi = L.DomUtil.create("a", "", c);
        const zo = L.DomUtil.create("a", "", c);
        zi.innerHTML = "+";
        zo.innerHTML = "âˆ’";
        L.DomEvent.on(zi, "click", () => map.zoomIn());
        L.DomEvent.on(zo, "click", () => map.zoomOut());
        return c;
      },
    });
    const ctrl = new ZoomControl({ position: "topright" }) as L.Control;
    map.addControl(ctrl);
    return () => map.removeControl(ctrl);
  }, [map]);
  return null;
};

/* ---------------- Main ---------------- */

const SHAPE_COLORS = [
  { stroke: "#ff6b6b", fill: "#ff8787" },
  { stroke: "#4dabf7", fill: "#74c0fc" },
  { stroke: "#f59f00", fill: "#ffc078" },
  { stroke: "#37b24d", fill: "#8ce99a" },
];

const MapView = () => {
  const [features, setFeatures] = useState<Feature<Geometry>[]>([]);
  const [searchText, setSearchText] = useState("");
  const [searchCoords, setSearchCoords] = useState<[number, number] | null>(null);
  const [theme, setTheme] = useState<"dark" | "light">("light");
  const mapRef = useRef<L.Map | null>(null);

  const onCreated = (e: any) => {
    const layer = e.layer;
    let geojson = layer.toGeoJSON() as Feature<Geometry>;
    const rawType = geojson.geometry.type.toLowerCase();

    const shapeType = rawType as ShapeType;

    /* âœ… SHAPE LIMIT CHECK (your code) */
    const count = features.filter(
      (f) => f.geometry.type.toLowerCase() === shapeType
    ).length;

    if (count >= MAX_SHAPES[shapeType]) {
      alert(`Maximum ${shapeType} limit reached`);
      layer.remove();
      return;
    }

    /* LineString â€” skip overlap */
    if (shapeType === "linestring") {
      geojson.properties = { shapeType };
      setFeatures((p) => [...p, geojson]);
      return;
    }

    /* Circle â†’ polygon */
    if (layer instanceof L.Circle) {
      const c = layer.getLatLng();
      geojson = turf.circle([c.lng, c.lat], layer.getRadius(), {
        steps: 64,
        units: "meters",
      }) as Feature<Geometry>;
    }

    /* âœ… OVERLAP FIX (NO CRASH) */
    if (features.length > 0) {
      const valid = features.filter(
        (f) => f.geometry.type !== "LineString"
      );
      if (valid.length && hasOverlap(geojson, valid)) {
        alert("âŒ Shape overlaps existing shape");
        layer.remove();
        return;
      }
    }

    const color = SHAPE_COLORS[Math.floor(Math.random() * SHAPE_COLORS.length)];
    geojson.properties = {
      shapeType,
      strokeColor: color.stroke,
      fillColor: color.fill,
    };

    setFeatures((p) => [...p, geojson]);
  };

  return (
    <div className="map-wrapper">
      {/* TOGGLE TOOLBAR */}
      <div className="toolbar-wrapper">
        <button
          className="toolbar-toggle"
          onClick={() =>
            document.querySelector(".toolbar-panel")?.classList.toggle("open")
          }
        >
          â˜°
        </button>

        <div className="toolbar-panel">
          <button
            onClick={() => {
              const blob = new Blob(
                [JSON.stringify({ type: "FeatureCollection", features }, null, 2)],
                { type: "application/json" }
              );
              const a = document.createElement("a");
              a.href = URL.createObjectURL(blob);
              a.download = "shapes.geojson";
              a.click();
            }}
          >
            â¬‡ Export
          </button>

          <button onClick={() => setTheme(theme === "dark" ? "light" : "dark")}>
            ðŸŒ— Theme
          </button>
        </div>
      </div>

      <MapContainer center={[20.5937, 78.9629]} zoom={5} zoomControl={false}>
        <MapRefSetter mapRef={mapRef} />
        <CustomZoom />

        <TileLayer
          url={
            theme === "dark"
              ? "https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png"
              : "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          }
        />

        <FeatureGroup>
          <EditControl
            position="topright"
            onCreated={onCreated}
            draw={{ polygon: true, rectangle: true, circle: true, polyline: true }}
          />
        </FeatureGroup>

        <DrawZoomControl />

        {features.map((f, i) => {
          const g = f.geometry.type.toLowerCase();
          if (g === "polygon")
            return (
              <Polygon
                key={i}
                positions={(f.geometry as any).coordinates[0].map((p: any) => [
                  p[1],
                  p[0],
                ])}
              />
            );
          if (g === "linestring")
            return (
              <Polyline
                key={i}
                positions={(f.geometry as any).coordinates.map((p: any) => [
                  p[1],
                  p[0],
                ])}
              />
            );
          return null;
        })}
      </MapContainer>
    </div>
  );
};

export default MapView;
