import { Feature, Polygon } from "geojson";
import * as turf from "@turf/turf";
import booleanContains from "@turf/boolean-contains";
import booleanIntersects from "@turf/boolean-intersects";

/**
 * ✅ Check if a new polygon fully contains or is fully contained by existing polygons
 */
export const applyPolygonRules = (
  newFeature: Feature<Polygon>,
  existing: Feature<Polygon>[]
): Feature<Polygon> | null => {
  for (const old of existing) {
    if (!old.geometry) continue;

    // Full containment → reject
    if (
      booleanContains(newFeature as any, old as any) ||
      booleanContains(old as any, newFeature as any)
    ) {
      return null;
    }
  }
  return newFeature;
};

/**
 * ✅ Detect if new polygon overlaps any existing polygons
 */
export const hasOverlap = (
  newFeature: Feature<Polygon>,
  existingFeatures: Feature<Polygon>[]
): boolean => {
  for (const f of existingFeatures) {
    if (booleanIntersects(newFeature as any, f as any)) return true;
  }
  return false;
};

/**
 * ✅ Trim polygon safely
 * - Partial overlaps trimmed
 * - Ignore tiny slivers (<1 m²)
 * - Return null if fully overlapped
 */
export const trimPolygon = (
  newPoly: Feature<Polygon>,
  existingPoly: Feature<Polygon>
): Feature<Polygon> | null => {
  try {
    if (!newPoly.geometry || !existingPoly.geometry) return null;

    // Step 1: Intersection check
    const intersection = (turf.intersect as any)(newPoly, existingPoly);

    if (!intersection) return newPoly; // No overlap

    // Ignore tiny overlaps
    if ((turf.area as any)(intersection) < 1) return newPoly;

    // Step 2: Trim overlapping part
    const trimmed = (turf.difference as any)(newPoly, existingPoly) as Feature<Polygon> | null;

    if (!trimmed) return null; // fully overlapped
    if ((turf.area as any)(trimmed) < 1) return null; // tiny sliver

    return trimmed;
  } catch (err) {
    console.warn("Auto-trim failed:", err);
    return null;
  }
};

/**
 * ✅ Utility: Convert Circle or Rectangle to Polygon
 */
export const toPolygon = (feature: Feature<any>): Feature<Polygon> => {
  if (feature.geometry.type === "Polygon") return feature as Feature<Polygon>;

  if (feature.properties?.radius && feature.geometry.type === "Point") {
    const [lng, lat] = feature.geometry.coordinates;
    const polygon = (turf.circle as any)([lng, lat], feature.properties.radius, {
      steps: 64,
      units: "meters",
    }) as Feature<Polygon>;
    polygon.properties = { ...feature.properties };
    return polygon;
  }

  if (feature.geometry.type === "Polygon" || feature.geometry.type === "Rectangle") {
    return feature as Feature<Polygon>;
  }

  throw new Error("Unsupported feature type for polygon conversion");
};
