import { useEffect, useRef, useState } from "react";
import {
  MapContainer,
  TileLayer,
  FeatureGroup,
  Marker,
  Tooltip,
  Polygon,
  Circle,
  Polyline,
  useMap,
} from "react-leaflet";
import { EditControl } from "react-leaflet-draw";
import L from "leaflet";
import * as turf from "@turf/turf";
import { Feature, Geometry } from "geojson";

import { hasOverlap } from "../utils/geometry";
import { MAX_SHAPES, ShapeType } from "../config";

import "leaflet/dist/leaflet.css";
import "leaflet-draw/dist/leaflet.draw.css";
import "../styles/map.css";

/* ---------------- Map Helpers ---------------- */

const MapRefSetter = ({ mapRef }: { mapRef: React.MutableRefObject<L.Map | null> }) => {
  const map = useMap();
  useEffect(() => {
    mapRef.current = map;
  }, [map]);
  return null;
};

const CustomZoom = () => {
  const map = useMap();
  useEffect(() => {
    const zoomIn = () => map.zoomIn();
    const zoomOut = () => map.zoomOut();
    window.addEventListener("map-zoom-in", zoomIn);
    window.addEventListener("map-zoom-out", zoomOut);
    return () => {
      window.removeEventListener("map-zoom-in", zoomIn);
      window.removeEventListener("map-zoom-out", zoomOut);
    };
  }, [map]);
  return null;
};

const DrawZoomControl = () => {
  const map = useMap();
  useEffect(() => {
    
    const ZoomControl = L.Control.extend({
      onAdd: function () {
        const container = L.DomUtil.create("div", "leaflet-bar draw-zoom-control");
        const zoomIn = L.DomUtil.create("a", "", container);
        zoomIn.innerHTML = "+";
        const zoomOut = L.DomUtil.create("a", "", container);
        zoomOut.innerHTML = "‚àí";

        L.DomEvent.on(zoomIn, "click", () => map.zoomIn());
        L.DomEvent.on(zoomOut, "click", () => map.zoomOut());
        return container;
      },
    });
    const control = new ZoomControl({ position: "topright" }) as L.Control;
    map.addControl(control);
    return () => map.removeControl(control);
  }, [map]);
  return null;
};

/* ---------------- Main Component ---------------- */

const SHAPE_COLORS = [
  { stroke: "#ff6b6b", fill: "#ff8787" },
  { stroke: "#4dabf7", fill: "#74c0fc" },
  { stroke: "#f59f00", fill: "#ffc078" },
  { stroke: "#37b24d", fill: "#8ce99a" },
];

const MapView = () => {
  const [features, setFeatures] = useState<Feature<Geometry>[]>([]);
  const [searchText, setSearchText] = useState("");
  const [searchCoords, setSearchCoords] = useState<[number, number] | null>(null);
  const [theme, setTheme] = useState<"dark" | "light">("light");
  const mapRef = useRef<L.Map | null>(null);

  const onCreated = (e: any) => {
    const layer = e.layer;
    let geojson = layer.toGeoJSON() as Feature<Geometry>;
    const rawType = geojson.geometry.type.toLowerCase();

    let shapeType: ShapeType;
    if (rawType === "linestring") shapeType = "linestring";
    else if (layer instanceof L.Circle) shapeType = "circle";
    else if (layer instanceof L.Rectangle) shapeType = "rectangle";
    else shapeType = "polygon";

    const count = features.filter(f => f.properties?.shapeType === shapeType).length;
    if (count >= MAX_SHAPES[shapeType]) {
      alert(`‚ùå Maximum ${shapeType} limit reached`);
      layer.remove();
      return;
    }

    if (shapeType === "linestring") {
      geojson.properties = { shapeType };
      setFeatures(prev => [...prev, geojson]);
      return;
    }

    if (layer instanceof L.Circle) {
      const c = layer.getLatLng();
      geojson = turf.circle([c.lng, c.lat], layer.getRadius(), {
        steps: 64,
        units: "meters",
      }) as Feature<Geometry>;
    }

    if (hasOverlap(geojson, features)) {
      alert("‚ùå Shape overlaps existing shape");
      layer.remove();
      return;
    }

    const color = SHAPE_COLORS[Math.floor(Math.random() * SHAPE_COLORS.length)];
    geojson.properties = { shapeType, strokeColor: color.stroke, fillColor: color.fill };
    setFeatures(prev => [...prev, geojson]);
  };

  const handleSearch = async () => {
    if (!searchText) return;
    const res = await fetch(
      `https://nominatim.openstreetmap.org/search?format=json&q=${searchText}`
    );
    const data = await res.json();
    if (data?.length) {
      const lat = +data[0].lat;
      const lon = +data[0].lon;
      setSearchCoords([lat, lon]);
      mapRef.current?.setView([lat, lon], 9);
    }
  };

  return (
    <div className="map-wrapper">

      {/* ‚úÖ TOGGLE TOOLBAR */}
      <div className="toolbar-wrapper">
        <button
          className="toolbar-toggle"
          onClick={() =>
            document.querySelector(".toolbar-panel")?.classList.toggle("open")
          }
        >
          ‚ò∞
        </button>

        <div className="toolbar-panel">
          <h3>Map Tools</h3>

          <input
            value={searchText}
            onChange={(e) => setSearchText(e.target.value)}
            placeholder="Search place"
          />
          <button onClick={handleSearch}>üîç</button>

          <button
            onClick={() => {
              const blob = new Blob(
                [JSON.stringify({ type: "FeatureCollection", features }, null, 2)],
                { type: "application/json" }
              );
              const a = document.createElement("a");
              a.href = URL.createObjectURL(blob);
              a.download = "shapes.geojson";
              a.click();
            }}
          >
            ‚¨á Export
          </button>

          <button onClick={() => setTheme(theme === "dark" ? "light" : "dark")}>
            üåó Theme
          </button>
        </div>
      </div>

      <MapContainer center={[20.5937, 78.9629]} zoom={5} zoomControl={false}>
        <MapRefSetter mapRef={mapRef} />
        <CustomZoom />

        <TileLayer
          url={
            theme === "dark"
              ? "https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png"
              : "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          }
        />

        <FeatureGroup>
          <EditControl
            position="topright"
            onCreated={onCreated}
            draw={{ polygon: true, rectangle: true, circle: true, polyline: true }}
          />
        </FeatureGroup>

        <DrawZoomControl />

        {searchCoords && (
          <Marker position={searchCoords}>
            <Tooltip permanent>{searchText}</Tooltip>
          </Marker>
        )}
      </MapContainer>
    </div>
  );
};

export default MapView;
